mapper.insert:
	INSERT INTO TO_DO_ITEM (
		TITLE,    CONTENT,    START_DT,   COMPLETED,    REG_DT
    	) 
    VALUES (
    	#{title}, #{content}, #{startDt}, #{completed}, #{regDt} 
		)

mapper.update:
UPDATE TO_DO_ITEM
   SET SEQ = #{seq}

      ,TITLE = #{title}


      ,CONTENT = #{content}


      ,START_DT = #{startDt}


      ,COMPLETED = #{completed}

      ,MODI_DT = #{modiDt}
 WHERE SEQ = #{seq}
